INSERT INTO `kinobot`.`actor` (`idactor`, `name`, `facts`, `biography`) VALUES (14, 'Данила Козловский', 'В детстве актёр совсем не отличался примерным поведением. Вместе с братьями — старшим, Егором, и младшим, Иваном, — он был не прочь пошалить, причём шалости эти граничили с хулиганством. Именно по этой причине Даниле пришлось сменить несколько школ: мальчика неоднократно отчисляли за плохое поведение.', 'Российский актёр театра и кино, кинорежиссёр, продюсер, сценарист; заслуженный артист Российской Федерации. Двукратный лауреат кинопремий «Золотой орёл» и «Ника», а также театральной премии «Золотая маска»  Родился: 3 мая 1985 г. (38 лет), Москва, РСФСР/СССР');
